import { createClient } from '@supabase/supabase-js'

// Fallback values untuk development - ganti dengan project Supabase Anda
const supabaseUrl = process.env.REACT_APP_SUPABASE_URL || 'https://your-project-id.supabase.co'
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY || 'your-anon-key-here'

// Validasi URL Supabase
const isValidUrl = (url) => {
  try {
    new URL(url);
    return url.startsWith('https://');
  } catch {
    return false;
  }
}

// Gunakan fallback jika URL tidak valid
const finalUrl = isValidUrl(supabaseUrl) ? supabaseUrl : 'https://demo.supabase.co'
const finalKey = supabaseAnonKey && supabaseAnonKey !== 'your-anon-key-here' ? supabaseAnonKey : 'demo-key'

console.log('Supabase Config:', { finalUrl, hasKey: !!finalKey })

export const supabase = createClient(finalUrl, finalKey, {
  auth: {
    persistSession: true,
    autoRefreshToken: true,
  }
})
